mapper.select:
select bno, id, title, content, reg_dt, writer, view_cnt, is_notice, frst_reg_dt, frst_reg_id, last_mode_dt, last_mode_id
FROM faq_board
WHERE bno = #{bno}

mapper.update:
UPDATE faq_board
SET title =#{title}
  ,content = #{content}
  ,writer = #{writer}
  ,is_notice = #{is_notice}
  ,last_mode_dt = now()
  ,last_mode_id = #{id}
WHERE bno = #{bno}

UPDATE faq_board
SET view_cnt = view_cnt +1
WHERE bno = #{bno}
