mapper.update:
UPDATE product
SET inv_amnt = inv_amnt + #{qty}
WHERE prod_num = #{prod_num}

UPDATE product
SET inv_amnt = inv_amnt - #{qty}
WHERE prod_num = #{prod_num}

UPDATE orders
SET ord_st_cd = 'C',
    cnl_dt = DATE_FORMAT(NOW(), '%Y-%m-%d %H:%i:%s'),
    last_mod_dt = DATE_FORMAT(NOW(), '%Y-%m-%d %H:%i:%s'),
    last_mod_id = #{cust_id}
WHERE ord_no = #{ord_no}

mapper.select:
SELECT prod_name
FROM product
WHERE prod_num = #{prod_num}

SELECT price
FROM product
WHERE prod_num = #{prod_num}

SELECT count(*)
FROM orders
WHERE cust_id = #{cust_id}
